mapper.select:
select count(*) 
from bbs6 








		bbs_name like #{find_name}	




	  	bbs_subject like #{find_name}




	  	bbs_content like #{find_name}

mapper.update:
update bbs6 
   set bbs_readcount=bbs_readcount+1 
 where bbs_num=#{num}

update bbs6 
   set bbs_name=#{bbs_name}, bbs_subject=#{bbs_subject},
       bbs_content=#{bbs_content}, 
       bbs_file=#{bbs_file, jdbcType=VARCHAR},
       bbs_original=#{bbs_original, jdbcType=VARCHAR}

 where bbs_num=#{bbs_num}

mapper.select:
select * from (select rownum rnum, bbs_num, bbs_name, bbs_pass, bbs_subject, bbs_content, bbs_file, bbs_re_ref, bbs_re_lev, bbs_re_seq, bbs_readcount, bbs_date, bbs_original from (select * from bbs6
bbs_name like #{find_name}
bbs_subject like #{find_name}
bbs_content like #{find_name}
order by bbs_re_ref desc, bbs_re_seq asc) ) where rnum >= ((#{page}-1) * 10 + 1) and rnum <= (#{page} * 10)

mapper.update:
        update bbs6 
           set bbs_re_seq=bbs_re_seq+1
		 where bbs_re_ref=#{bbs_re_ref}  and bbs_re_seq > #{bbs_re_seq}
